def _dedup(seq: List[str]) -> List[str]:
    seen = set()
    out = []
    for s in seq:
        if s not in seen:
            seen.add(s)
            out.append(s)
    return out


TRACKERS = _dedup(TRACKERS)


def _real_home() -> str:
    """Return invoking user's home even when running under sudo."""
    u = os.environ.get("SUDO_USER")
    if u:
        try:
            return pwd.getpwnam(u).pw_dir
        except Exception:
            pass
    return os.path.expanduser("~")


def pull(links: Iterable[str],
         dest: str = "/Volumes/Elements 1/music_raw",
         concurrent: int = 1,
         file_allocation: str = "none") -> None:
    """
    Download magnet URIs to `dest` via aria2c with sudo-safe defaults.
    """
    if shutil.which("aria2c") is None:
        raise FileNotFoundError("aria2c not found. Install it (e.g., `brew install aria2`).")
    if not os.path.isdir(dest):
        raise FileNotFoundError(f"Destination folder not found: {dest}")

    # Write test (catches read-only mounts)
    with tempfile.NamedTemporaryFile(dir=dest, prefix=".write_test_", delete=True):
        pass

    mags: List[str] = [s.strip() for s in links if isinstance(s, str) and s.strip()]
    mags = [s for s in mags if s.startswith("magnet:?")]
    if not mags:
        raise ValueError("No valid magnet links provided (must start with 'magnet:?').")

    # Working trackers (simplified list)
    working_trackers = [
        "udp://tracker.openbittorrent.com:6969",
        "udp://open.demonii.com:1337",
        "udp://tracker.opentrackr.org:1337",
        "udp://exodus.desync.com:6969",
        "udp://tracker.coppersurfer.tk:6969"
    ]

    # Add working trackers to magnet links that need them
    enhanced_mags = []
    for mag in mags:
        if "&tr=" not in mag:  # No trackers in magnet
            tracker_params = "".join(f"&tr={tracker}" for tracker in working_trackers)
            enhanced_mags.append(mag + tracker_params)
        else:
            enhanced_mags.append(mag)

    home = _real_home()
    aria2_dir = os.path.join(home, ".aria2")
    os.makedirs(aria2_dir, exist_ok=True)

    log_path = os.path.join(dest, f"aria2-{datetime.now():%Y%m%d_%H%M%S}.log")

    # Write enhanced magnets to temp file
    with tempfile.NamedTemporaryFile(mode="w", prefix="magnets_", suffix=".txt", delete=False) as tf:
        tf.write("\n".join(enhanced_mags) + "\n")
        in_file = tf.name

    # Simplified command focusing on what works
    cmd = [
        "aria2c",
        "-i", in_file,
        f"--dir={dest}",
        "--seed-time=0",
        "--max-upload-limit=0",
        f"--max-concurrent-downloads={int(concurrent)}",
        f"--log={log_path}",
        "--log-level=notice",
    ]

    env = os.environ.copy()
    env["HOME"] = home

    try:
        rc = subprocess.run(cmd, env=env).returncode
        if rc != 0:
            raise RuntimeError(f"aria2c failed with exit code {rc}. See log: {log_path}")
    finally:
        try:
            os.remove(in_file)
        except Exception:
            pass

